def get_data_samples(channel, basedir, postfix, selection):
    if   channel [0] == 'm': lep = 'mu'
    elif channel [0] == 'e': lep = 'ele'
    assert lep == 'ele' or lep == 'mu', 'Lepton flavor error'
    data = [
        Sample('Single_{lep}_2016C'.format(lep=lep), ['Single_{lep}_2016C'.format(lep=lep)], channel, '2016C', selection, 'data_obs', 'black', 9999, '/'.join([basedir, 'data']), postfix, True, False, False, 1., 1., year=2016),
        Sample('Single_{lep}_2016D'.format(lep=lep), ['Single_{lep}_2016D'.format(lep=lep)], channel, '2016D', selection, 'data_obs', 'black', 9999, '/'.join([basedir, 'data']), postfix, True, False, False, 1., 1., year=2016),
        Sample('Single_{lep}_2016E'.format(lep=lep), ['Single_{lep}_2016E'.format(lep=lep)], channel, '2016E', selection, 'data_obs', 'black', 9999, '/'.join([basedir, 'data']), postfix, True, False, False, 1., 1., year=2016),
        Sample('Single_{lep}_2016F'.format(lep=lep), ['Single_{lep}_2016F'.format(lep=lep)], channel, '2016F', selection, 'data_obs', 'black', 9999, '/'.join([basedir, 'data']), postfix, True, False, False, 1., 1., year=2016),
        Sample('Single_{lep}_2016G'.format(lep=lep), ['Single_{lep}_2016G'.format(lep=lep)], channel, '2016G', selection, 'data_obs', 'black', 9999, '/'.join([basedir, 'data']), postfix, True, False, False, 1., 1., year=2016),
        Sample('Single_{lep}_2016H'.format(lep=lep), ['Single_{lep}_2016H'.format(lep=lep)], channel, '2016H', selection, 'data_obs', 'black', 9999, '/'.join([basedir, 'data']), postfix, True, False, False, 1., 1., year=2016),
    ]
    return data

